export const SPRITE_BASE_URL = "https://play.pokemonshowdown.com/sprites/gen5";

export function normalizeName(value: string): string {
  return value.toLowerCase().replace(/[^a-z0-9]+/g, '');
}

export function buildSpriteUrl(displayName: string): string | null {
  const normalized = normalizeName(displayName);
  if (!normalized) return null;
  const spriteId = SPRITE_ID_LOOKUP[normalized] ?? normalized;
  if (!spriteId) return null;
  return `${SPRITE_BASE_URL}/${spriteId}.png`;
}
